'use strict'; ///////////////////////////////////////////////////
// Создание слайдера

var slides = document.querySelectorAll('.slide');
var btnLeft = document.querySelector('.slider__btn--left');
var btnRight = document.querySelector('.slider__btn--right');
var dotContainer = document.querySelector('.dots');
var currentSlide = 0;
var slidesNumber = slides.length; // const slider = document.querySelector('.slider');
// slider.style.transform = 'scale(0.4) translateX(-1030px)';
// slider.style.overflow = 'visible';

var createDots = function createDots() {
  slides.forEach(function (_, index) {
    dotContainer.insertAdjacentHTML('beforeend', "<button class=\"dots__dot\" data-slide=\"".concat(index, "\"></button>"));
  });
};

createDots();

var activateCurrentDot = function activateCurrentDot(slide) {
  document.querySelectorAll('.dots__dot').forEach(function (dot) {
    return dot.classList.remove('dots__dot--active');
  });
  document.querySelector(".dots__dot[data-slide=\"".concat(slide, "\"]")).classList.add('dots__dot--active');
};

activateCurrentDot(0);

var moveToSlide = function moveToSlide(slide) {
  slides.forEach(function (s, index) {
    return s.style.transform = "translateX(".concat((index - slide) * 100, "%)");
  });
};

moveToSlide(0);

var nextSlide = function nextSlide() {
  if (currentSlide === slidesNumber - 1) {
    currentSlide = 0;
  } else {
    currentSlide++;
  }

  moveToSlide(currentSlide);
  activateCurrentDot(currentSlide); // 1 - -100%; 2 - 0; 3 - 100%; 4 - 200%;)
};

var previousSlide = function previousSlide() {
  if (currentSlide === 0) {
    currentSlide = slidesNumber - 1;
  } else {
    currentSlide--;
  }

  moveToSlide(currentSlide);
  activateCurrentDot(currentSlide); // 1 - -100%; 2 - 0; 3 - 100%; 4 - 200%
};

btnRight.addEventListener('click', nextSlide);
btnLeft.addEventListener('click', previousSlide);
document.addEventListener('keydown', function (e) {
  // console.log(e);
  if (e.key === 'ArrowRight') nextSlide();
  if (e.key === 'ArrowLeft') previousSlide();
});
dotContainer.addEventListener('click', function (e) {
  if (e.target.classList.contains('dots__dot')) {
    var slide = e.target.dataset.slide;
    moveToSlide(slide);
    activateCurrentDot(slide);
  }
});
